      // Page has no manifest or was invalid JSON.
      return CacheStartUrl.generateAuditResult({
        rawValue: false,
      });
    }

    const manifest = artifacts.Manifest.value;
    if (!(manifest.start_url && manifest.start_url.value)) {
      return CacheStartUrl.generateAuditResult({
        rawValue: false,
